sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	// Add your code here
	var author = target.getValue("author");
	var index = -1;
	index = author.indexOf(' ');  // Gets the first index where a space occours
	var text1='';
	var text2='';
	if(index>-1)
		{
		text1 = author.substr(0, index); // Gets the first part
		text2 = author.substr(index + 1);
		}
	else 
		{
			text1=author;
		}
	var gr = new GlideRecord('authors');
	gr.addQuery('author','=',author);
	gr.query();
	while(gr.hasNext())
	{
		if(gr.getValue("name")=='' || gr.getValue("name")==null)
		{
			gr.setValue("name",text1);
			gr.setValue("surname",text2);
			gr.update(); //update record.
		}
	}

})(source, map, log, target);
